import { pi } from './pi.js';

describe('pi', () => {
    test('returns a string', () => {
        expect(typeof pi()).toBe('string');
    });

    test('default count (=0) returns ""', () => {
        expect(pi()).toBe('');
    });

    test('count=1 returns "3"', () => {
        expect(pi(1)).toBe('3');
    });

    test('count=2 returns "3."', () => {
        expect(pi(2)).toBe('3.');
        expect(pi(3)).toBe('3.1');
        expect(pi(6)).toBe('3.1415');
    });

    test('known prefixes of pi for small counts', () => {
        expect(pi(3)).toBe('3.1');
        expect(pi(4)).toBe('3.14');
        expect(pi(5)).toBe('3.141');
        expect(pi(6)).toBe('3.1415');
        expect(pi(11)).toBe('3.141592653'); // 11 символов
        expect(pi(1002)).toBe(
            '3.'
        );
    });

    test('for count >= 2 the result has a dot after the first digit', () => {
        const s = pi(20);
        expect(s[0]).toBe('3');
        expect(s[1]).toBe('.');
    });

    test('result contains only digits and at most one dot', () => {
        const s = pi(30);
        expect(s).toMatch(/^[0-9.]+$/);
        const dots = (s.match(/\./g) || []).length;
        expect(dots).toBeLessThanOrEqual(1);
    });
});
